import React,{ useState } from 'react';



const Todo = ()=>{

    const [item, setItem] = useState('');
    const [completed, setCompleted] = useState('false');
    const [todolist, setTodoList] = useState([]);
    
    const handleSubmit = (e) => {
        e.preventDefault(); 
        const singleItem = { id: new Date().getTime().toString(), item, completed };
        console.log(singleItem);
        setTodoList((todolist) => {
            return [...todolist, singleItem];
          });
    }


    return(
    <>
    <div class="flex flex-col justify-center items-center bg-gray-400">
    <form action="" onSubmit={handleSubmit} class="h-auto ">
            <div class="flex flex-col justify-center items-center  md:w-1/4 space-y-4  mt-10 ">
                <input type="item" name="item" id="item" value={item} class="text-center md:w-4/5 border-2 focus:outline-none rounded p-1  bg-gray-100" onChange={(e) => setItem(e.target.value)}  />
                <button type='submit' class="focus:outline-none bg-gray-500 hover:bg-gray-400 text-white font-bold w-2/3 py-2 px-4 rounded" >Add Item</button>
            </div>
            
    </form>
  
        
        <div class="bg-yellow-900">
             { todolist.map((todo,key)=>{   
                 const {item,completed} =todo;
                 return(
                     <div>
                         <h4>{ item }</h4>   
                        <h6>{ completed }</h6>
                     </div>
                
                );
                } )}

        </div>
     
    </div>    
        
     </>
        

    )
} 


export default Todo;
