import React from "react";

const Todo = () => {
  const [todos, setTodos] = React.useState([]);
  const [todo, setTodo] = React.useState("");
  const [editid, setEditId]=React.useState(null);
  const [edittext, setEditText]=React.useState(true);


  function handleSubmit(e) {
    e.preventDefault();

    const newTodo = {
      id: new Date().getTime(),
      text: todo,
      completed: false,
    };
    setTodos([...todos].concat(newTodo));
    setTodo("");
  }


  function deleteTodo(id) {
    let updatedTodos = [...todos].filter((todo) => todo.id !== id);
    setTodos(updatedTodos);
  }

  function toggleComplete(id) {
    let updatedTodos = [...todos].map((todo) => {
      if (todo.id === id) {
        todo.completed = !todo.completed;
      }
      return todo;
    });
    setTodos(updatedTodos);
  }

  function editHandleSubmit(id)
  {
    const updatedTodos = [...todos].map((todo) => {
      if (todo.id === id) {
        todo.text = edittext;
      }
      return todo;
    });
     setTodos(updatedTodos);
    setTodoEditing(null);
  }



    return(

        <>
      
        <form action="" class="h-auto flex justify-center w-full ">
            <div class="flex flex-col justify-center items-center  md:w-1/4 space-y-4  mt-10 ">
            {todo.id === editid ? (
              <>
              <input type="text"  value={todo} placeholder="Add a Todo" class="text-center md:w-4/5 border-2 focus:outline-none rounded p-1  bg-gray-0" onChange={(e) => setEditText(e.target.value)}  />
                <button onClick={ ()=> {editHandleSubmit(todo.id)}} type='submit' class="focus:outline-none bg-gray-800 hover:bg-gray-700 text-white font-bold md:w-2/5 w-2/3 py-2 px-4 rounded" >Edit Item</button></>
                
            ) : (
              <>
              <input type="text"  value={todo} placeholder="Add a Todo" class="text-center md:w-4/5 border-2 focus:outline-none rounded p-1  bg-gray-0" onChange={(e) => setTodo(e.target.value)}  />
                <button onClick={handleSubmit} type='submit' class="focus:outline-none bg-gray-800 hover:bg-gray-700 text-white font-bold md:w-2/5 w-2/3 py-2 px-4 rounded" >Add Item</button>
              </>
                
            )}

            </div>
            
        </form>

        <div  class="mt-2 md:mt-1 w-full flex justify-center ">
        <div class="md:mt-4 w-full md:w-2/6  flex flex-col justify-center px-5 md:px-0">
        {todos.map((todo)=>
          <div class=" flex flex-col justify-center border-2 mt-4 bg-white border-gray-200 p-3 rounded-md" key={todo.id}>
          <div class="flex justify-between pl-4" >
              <div class="mx-auto font-bold text-base text-gray-800"> { todo.text } </div>
              <div>
              <input
            type="checkbox"
            id="completed"
            class="h-5 w-5"
            checked={todo.completed}
            onChange={() => toggleComplete(todo.id)}
         />
              </div>
          
            </div> 
            <div class="flex justify-between mt-4">
          <button onClick={() => setEditId(todo.id)} class="flex-no-shrink md:p-3 ml-2   font-bold text-gray-800  bg-green-400 rounded hover:bg-green-300 px-6 md:w-1/4">Edit</button>
          <button onClick={() => deleteTodo(todo.id)} class="flex-no-shrink p-3 ml-2   font-bold text-gray-800  bg-red-400 rounded hover:bg-red-300  md:w-1/4">Remove</button>
          {todo.completed ? (
              <div class="flex items-center " > <div class="p-2 text-base font-semibold">Completed</div>  <img src="https://img.icons8.com/fluent/30/000000/checked-2.png"/>
              </div>
                ) : ( 
            <div class="flex items-center ">
                 <div class="p-2 font-semibold text-base"></div>  <img src="https://img.icons8.com/emoji/25/000000/cross-mark-emoji.png"/>

              </div>
              )} 
            </div>
      
               







      </div>
  )}
        </div>
     

        </div>
          

      
                        
        
        </>
    )


}
    

export default Todo;
