import React,{ useState } from 'react';



const Todo = ()=>{

    const [item, setItem] = useState('');
    const [completed, setCompleted] = useState(false);
    const [todolist, setTodoList] = useState([]);
    
    const handleSubmit = (e) => {
        e.preventDefault();     
        const singleItem = { id: new Date().getTime().toString(), item, completed };
        setTodoList((todolist) => {
            return ([...todolist].concat(singleItem));
            setItem("")
          });
    }

    function deleteTodo(id) {
        let updatedTodos = [...todolist].filter((todo) => todo.id !== id);
        setTodoList(updatedTodos);
      }


    const changeComplete = (id) =>
    {
        let updatedTodos = todolist.map(todo =>{
            if (todo.id === id)
            {
                todo.completed = !todo.completed;
            }
            return todo;
        } );
        setTodoList(updatedTodos);
    }

    function deleteTodo(id) {
        let updatedTodos = [...todolist].filter((todo) => todo.id !== id);
        setTodoList(updatedTodos);
      }


    return(
    <>
     <div class="flex flex-col justify-center items-center bg-gray-400">
     <form action="" onSubmit={handleSubmit} class="h-auto flex justify-center w-full bg-blue-400">
            <div class="flex flex-col justify-center items-center  md:w-1/4 space-y-4  mt-10 ">
                <input type="item" name="item"  onChange={(e) => setItem(e.target.value)} id="item" value={item} class="text-center md:w-4/5 border-2 focus:outline-none rounded p-1  bg-gray-100" onChange={(e) => setItem(e.target.value)}  />
                <button type='submit' class="focus:outline-none bg-gray-500 hover:bg-gray-400 text-white font-bold w-2/3 py-2 px-4 rounded" >Add Item</button>
            </div>
            
        </form>
  
        
        <div class="border-4 mt-6 w-1/3 p-4 mt-3">
             
             { todolist.map((todo,id)=>{   
                    
                 return(
                     <div>
                         
                         <h4>{ item }</h4>   
                        <h6>{ completed }</h6>
                        <button onClick={() => deleteTodo(todo.id)} class="flex-no-shrink p-2 ml-2 border-2 rounded text-red border-red hover:text-white hover:bg-red">Remove</button>
                        { completed ?(
                        <button onSubmit={() =>{changeComplete(todo.id)}} class="flex-no-shrink p-2 ml-4 mr-2 border-2 rounded hover:text-white text-green border-green hover:bg-red">Done</button>
                        ):
                        (<button onSubmit={() =>{changeComplete(todo.id)}} class="flex-no-shrink p-2 ml-2 border-2 rounded text-red border-red hover:text-white hover:bg-green">Not Done</button> ) }
                     </div>
                         );

                        })}

        </div>
     
    </div>    
        
    </>
            )
    
        

    
                
}


export default Todo;
