import React from "react";

const Todo = () => {
  const [todos, setTodos] = React.useState([]);
  const [todo, setTodo] = React.useState("");



  function handleSubmit(e) {
    e.preventDefault();

    const newTodo = {
      id: new Date().getTime(),
      text: todo,
      completed: false,
    };
    setTodos([...todos].concat(newTodo));
    setTodo("");
  }
  

  function deleteTodo(id) {
    let updatedTodos = [...todos].filter((todo) => todo.id !== id);
    setTodos(updatedTodos);
  }

  function toggleComplete(id) {
    let updatedTodos = [...todos].map((todo) => {
      if (todo.id === id) {
        todo.completed = !todo.completed;
      }
      return todo;
    });
    setTodos(updatedTodos);
  }

  



    return(

        <>
        <form action="" onSubmit={handleSubmit} class="h-auto flex justify-center w-full bg-blue-400">
            <div class="flex flex-col justify-center items-center  md:w-1/4 space-y-4  mt-10 ">
                <input type="text"  value={todo} class="text-center md:w-4/5 border-2 focus:outline-none rounded p-1  bg-gray-100" onChange={(e) => setTodo(e.target.value)}  />
                <button type='submit' class="focus:outline-none bg-gray-500 hover:bg-gray-400 text-white font-bold w-2/3 py-2 px-4 rounded" >Add Item</button>
            </div>
            
        </form>
        {todos.map((todo)=>{
            <div>todo.text</div>
        })}

        {/* <button onClick={() => deleteTodo(todo.id)} class="flex-no-shrink p-2 ml-2 border-2 rounded text-red border-red hover:text-white hover:bg-red">Remove</button>
                        { completed ?(
                        <button onSubmit={() =>{changeComplete(todo.id)}} class="flex-no-shrink p-2 ml-4 mr-2 border-2 rounded hover:text-white text-green border-green hover:bg-red">Done</button>
                        ):
                        (<button onSubmit={() =>{changeComplete(todo.id)}} class="flex-no-shrink p-2 ml-2 border-2 rounded text-red border-red hover:text-white hover:bg-green">Not Done</button> ) }
                         );
                        } */}
                        
        
        </>
    )


}
    

export default Todo;
