import React,{ useState } from 'react';



const Todo = ()=>{

    const [item, setItem] = useState('');
    const [completed, setCompleted] = useState('false');
    const [todolist, setTodoList] = useState([]);
    
    const handleSubmit = (e) => {
        e.preventDefault(); 
        const singleItem = { id: new Date().getTime().toString(), item, completed };
        console.log(singleItem);
        setTodoList((todolist) => {
            return [...todolist, singleItem];
          });
    }


    return(
        <>
        <form action="" onSubmit={handleSubmit} class="h-screen flex justify-center">
            <div class="grid justify-items-center h-1/5 md:h-1/5 md:w-1/4 space-y-4  mt-10 ">
                <input type="item" name="item" id="item" value={item} class="text-center w-4/5 border-2 focus:outline-none rounded p-1  bg-gray-100" onChange={(e) => setItem(e.target.value)}  />
                <button type='submit' class="focus:outline-none bg-gray-500 hover:bg-gray-400 text-white font-bold py-2 px-4 rounded" >Add Item</button>
            </div>
            
        </form>
        <div>
             { todolist.map((todo)=>{   
                 const {item,completed} =todo;
                 return(
                     <div>
                         <h4>{ item }</h4>   
                        <h6>{ completed }</h6>
                     </div>
                
                )} )}

        </div>
        </>
        

    )
} 


export default Todo;
